const Emitter = require('events')

const aboutConfig = require('./config').section
const emtrID = new Emitter();
emtrID.on(aboutConfig.section, function () {
   return 'about'
})


const bckgrdConfig = require('./config').background;
const emtrLightBg = new Emitter();
emtrLightBg.on(bckgrdConfig.lightBg, function () {
  return ''
})

function defaultObj(
  id,lightBg,lightText,lightTextDesc,topLine,headline,description,
  buttonLabel,imgStart,
  img,
  alt,
  dark,
  primary,
  darkText,
) {
  return {
    id: id, lightBg: lightBg, lightText: lightText,
    lightTextDesc: lightTextDesc, topLine: topLine,
    headline: headline, description: description,
    buttonLabel: buttonLabel, imgStart: imgStart,
    img: img, alt: alt, dark: dark, primary: primary,
    darkText: darkText,
  };
}


export const homeObjOne = defaultObj(
  "about",
  false,
  true,
  true,
  "Premium Bank",
  "Unlimited Transactions with zero fees",
  "Get access to our exclusive app that allows you to send unlimited transactions without getting charged any fees",
  "Get started",
  false,
  require("../../images/svg-1.svg").default,
  "Car",
  true,
  true,
  false
);


export const homeObjTwo = defaultObj(
  "discover",
  true,
  false,
  false,
  "Unlimited Access",
  "Login to your account at any time.",
  "We have you covered no matter where you are located. All you need is an internet connection and a phone or computer",
  "Learn more",
  true,
  require("../../images/svg-2.svg").default,
  "Piggybank",
  false,
  false,
  true,
);

export const homeObjThree = {
  id: "signup",
  lightBg: true,
  lightText: false,
  lightTextDesc: false,
  topLine: "Join our Team",
  headline: "Creating an account is extremely easy",
  description:
    "Get everything set up and ready in under 10 minutes. All you need to do is add your information and you're ready to go.",
  buttonLabel: "Start Now",
  imgStart: true,
  img: require("../../images/svg-3.svg").default,
  alt: "Paper",
  dark: false,
  primary: false,
  darkText: true,
};


